@Composable
fun CardPromo(url:String) {
    Card(
        modifier = Modifier
            .height(180.dp)
            .width(300.dp)
    ) {
        Image(
            painter = rememberAsyncImagePainter(url),
            contentDescription = "promociones",
            modifier = Modifier.fillMaxSize(),
            contentScale = ContentScale.FillBounds
        )


    }

}

